import React, { useState, useEffect } from 'react';

function Quiz({ question, userAnswer, handleAnswer }) {
  const [timeLeft, setTimeLeft] = useState(6); // Timer set for 6 seconds

  useEffect(() => {
    // Reset the timer whenever a new question is presented
    setTimeLeft(6);

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleAnswer(); // Automatically submit answer when time runs out
          return 0; // Prevent going below 0
        }
        return prev - 1; // Decrement timer
      });
    }, 1000); // Update every second

    return () => clearInterval(timer); // Cleanup on unmount
  }, [question]); // Dependency on the current question

  return (
    <div className="quiz">
      <p className="question-text">What is {question.num1} x {question.num2}?</p>
      <input
        type="text"
        value={userAnswer}
        readOnly
        placeholder="Your answer"
      />
      <div className="timer">Time left: {timeLeft}s</div> {/* Display timer */}
    </div>
  );
}

export default Quiz;
