// src/App.js
import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import Quiz from './components/Quiz';
import Scoreboard from './components/Scoreboard';
import NumberPad from './components/NumberPad';
import NumberSelection from './components/NumberSelection';

function App() {
  const [score, setScore] = useState(0);
  const [question, setQuestion] = useState(null);
  const [userAnswer, setUserAnswer] = useState('');
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [showScore, setShowScore] = useState(false);
  const [questionsRemaining, setQuestionsRemaining] = useState(25);
  const [timeLeft, setTimeLeft] = useState(6);

  // Start Quiz with Selected Numbers
  const startQuiz = (numbers) => {
    setSelectedNumbers(numbers);
    setScore(0);
    setQuestionsRemaining(25);
    setShowScore(false);
    setQuestion(generateQuestion(numbers));
    setTimeLeft(6); // Reset timer for the first question
  };

  // Generate Question Based on Selected Numbers
  const generateQuestion = (numbers) => {
    let num1, num2;

    if (numbers.includes('square')) {
      num1 = num2 = Math.floor(Math.random() * 11) + 2; // Square number from 2 to 12
    } else {
      num1 = numbers[Math.floor(Math.random() * numbers.length)];
      num2 = Math.floor(Math.random() * 12) + 1; // Random multiplier 1 to 12
    }

    return { num1, num2, answer: num1 * num2 };
  };

  // Handle Answer Submission
  const handleAnswer = useCallback(() => {
    if (parseInt(userAnswer) === question.answer) {
      setScore(score + 1);
    }

    // Check if there are questions remaining
    if (questionsRemaining > 1) {
      setQuestionsRemaining(questionsRemaining - 1);
      setQuestion(generateQuestion(selectedNumbers));
      setUserAnswer('');
      setTimeLeft(6); // Reset timer for next question
    } else {
      setShowScore(true); // Show final score after last question
    }
  }, [question, userAnswer, score, questionsRemaining, selectedNumbers]);

  // Timer Effect
  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else {
      handleAnswer(); // Automatically move to the next question when time runs out
    }
  }, [timeLeft, handleAnswer]);

  return (
    <div className="App">
      {showScore ? (
        <Scoreboard score={score} />
      ) : selectedNumbers.length > 0 ? (
        <>
          <Quiz
            question={question}
            userAnswer={userAnswer}
            setUserAnswer={setUserAnswer}
            timeLeft={timeLeft}
          />
          <NumberPad
            onNumberClick={(num) => setUserAnswer(userAnswer + num)}
            onClear={() => setUserAnswer('')}
            onDelete={() => setUserAnswer(userAnswer.slice(0, -1))}
            handleAnswer={handleAnswer}
          />
        </>
      ) : (
        <NumberSelection onStart={startQuiz} />
      )}
    </div>
  );
}

export default App;
