import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import Quiz from './components/Quiz';
import Scoreboard from './components/Scoreboard';
import NumberPad from './components/NumberPad';

function App() {
  const [score, setScore] = useState(0);
  const [question, setQuestion] = useState(generateQuestion());
  const [userAnswer, setUserAnswer] = useState('');
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [timeLeft, setTimeLeft] = useState(6);
  const [showScore, setShowScore] = useState(false);

  function generateQuestion() {
    const num1 = selectedNumbers[Math.floor(Math.random() * selectedNumbers.length)];
    const num2 = Math.floor(Math.random() * 12) + 1; // Questions up to 12
    return { num1, num2, answer: num1 * num2 };
  }

  // Using useCallback to memoize handleAnswer
  const handleAnswer = useCallback(() => {
    if (
      question &&
      parseInt(userAnswer) === question.answer
    ) {
      setScore(score + 1);
    }

    setUserAnswer('');
    setTimeLeft(6); // Reset time for next question

    if (currentQuestionIndex < 24) { // Assuming you want 25 questions (0-24)
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setQuestion(generateQuestion());
    } else {
      setShowScore(true); // Show score at the end
    }
  }, [question, userAnswer, score, currentQuestionIndex, selectedNumbers]);

  // Timer effect
  useEffect(() => {
    if (showScore) return; // Don't start timer if showing score

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleAnswer(); // Call handleAnswer when time runs out
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer); // Cleanup interval on unmount
  }, [timeLeft, showScore, handleAnswer]);

  // Number pad functions
  function handleNumberClick(number) {
    setUserAnswer(userAnswer + number); // Append the clicked number to the answer
  }

  function handleClear() {
    setUserAnswer(''); // Clear the answer field
  }

  function handleDelete() {
    setUserAnswer(userAnswer.slice(0, -1)); // Remove the last digit
  }

  return (
    <div className="App">
      <h1>Times Tables Quiz</h1>
      <Scoreboard score={showScore ? score : 0} />
      <Quiz
        question={question}
        userAnswer={userAnswer}
        setUserAnswer={setUserAnswer}
      />
      <NumberPad 
        onNumberClick={handleNumberClick} 
        onClear={handleClear} 
        onDelete={handleDelete} 
        handleAnswer={handleAnswer}
      />
      <div>Time Left: {timeLeft}</div>
    </div>
  );
}

export default App;
