import React, { useState, useCallback, useEffect } from 'react';
import './App.css';
import NumberSelection from './components/NumberSelection';
import Quiz from './components/Quiz';

function App() {
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [includeSquares, setIncludeSquares] = useState(false);
  const [question, setQuestion] = useState(null); // State to hold the current question

  const startQuiz = (numbers, squares) => {
    setSelectedNumbers(numbers);
    setIncludeSquares(squares);
  };

  const generateQuestion = useCallback(() => {
    if (selectedNumbers.length === 0) return null;

    const selectedNum = selectedNumbers[Math.floor(Math.random() * selectedNumbers.length)];
    const rangeLimit = selectedNum >= 11 ? 12 : 10;
    let num1, num2;

    if (includeSquares && Math.random() > 0.5) {
      num1 = selectedNum;
      num2 = selectedNum;
    } else {
      const randomNum = Math.floor(Math.random() * rangeLimit) + 1;
      [num1, num2] = Math.random() > 0.5 ? [selectedNum, randomNum] : [randomNum, selectedNum];
    }

    return { num1, num2, answer: num1 * num2 };
  }, [selectedNumbers, includeSquares]); // Include includeSquares as a dependency

  // Effect to generate the first question when quiz starts
  useEffect(() => {
    if (selectedNumbers.length > 0) {
      setQuestion(generateQuestion());
    }
  }, [selectedNumbers, generateQuestion]);

  return (
    <div className="App">
      <h1>Times Table Quiz</h1>
      {selectedNumbers.length === 0 ? (
        <NumberSelection onStart={startQuiz} />
      ) : (
        <Quiz question={question} generateQuestion={generateQuestion} />
      )}
    </div>
  );
}

export default App;
