// NumberSelection.js
import React from 'react';

const NumberSelection = ({ onStart }) => {
  const numbers = Array.from({ length: 12 }, (_, i) => i + 1); // Numbers from 1 to 12
  const [selectedNumbers, setSelectedNumbers] = React.useState([]);

  const toggleNumber = (number) => {
    setSelectedNumbers((prev) => 
      prev.includes(number) ? prev.filter(n => n !== number) : [...prev, number]
    );
  };

  const handleSubmit = () => {
    onStart(selectedNumbers);
  };

  return (
    <div>
      <h2>Select Times Table Numbers</h2>
      <div>
        {numbers.map(number => (
          <button 
            key={number} 
            onClick={() => toggleNumber(number)} 
            style={{ backgroundColor: selectedNumbers.includes(number) ? 'lightblue' : 'lightgray' }}
          >
            {number}
          </button>
        ))}
      </div>
      <button onClick={handleSubmit} disabled={selectedNumbers.length === 0}>Start Quiz</button>
    </div>
  );
};

export default NumberSelection;
