import React, { useState, useEffect } from 'react';
import './App.css';
import FrontPage from './components/FrontPage';
import Quiz from './components/Quiz';

function App() {
  const [selectedNumber, setSelectedNumber] = useState(null); // Track chosen times table
  const [questions, setQuestions] = useState([]);             // Array of questions
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track current question
  const [userAnswer, setUserAnswer] = useState('');           // User input
  const [score, setScore] = useState(0);                      // Score tracking
  const [showScore, setShowScore] = useState(false);          // Control final score display
  const [timeLeft, setTimeLeft] = useState(6);                // Countdown timer

  // Generate 25 questions based on the selected number
  useEffect(() => {
    if (selectedNumber !== null) {
      const newQuestions = Array.from({ length: 25 }, () => {
        const multiplier = Math.floor(Math.random() * 12) + 1;
        return {
          num1: selectedNumber,
          num2: multiplier,
          answer: selectedNumber * multiplier,
        };
      });
      setQuestions(newQuestions);
    }
  }, [selectedNumber]);

  // Handle answer submission and move to the next question
  function handleAnswer() {
    if (
      questions.length > 0 && 
      questions[currentQuestionIndex] && 
      parseInt(userAnswer) === questions[currentQuestionIndex].answer
    ) {
      setScore(score + 1);
    }

    setUserAnswer('');      // Clear answer input
    setTimeLeft(6);         // Reset timer for the next question

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1); // Move to next question
    } else {
      setShowScore(true);    // Show score after last question
    }
  }

  // Timer countdown logic
  useEffect(() => {
    if (selectedNumber === null || showScore) return; // Skip if no number is selected or quiz is complete

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          handleAnswer(); // Automatically submit answer if time runs out
          return 0;
        }
        return prev - 1; // Decrement timer
      });
    }, 1000);

    return () => clearInterval(timer); // Cleanup on component unmount
  }, [currentQuestionIndex, selectedNumber, showScore]);

  // Render the front page if no number is selected; otherwise, show quiz or score
  return (
    <div className="App">
      {selectedNumber === null ? (
        <FrontPage onSelectNumber={setSelectedNumber} />
      ) : showScore ? (
        <div>
          <h1>Quiz Complete!</h1>
          <p>Your Score: {score} / 25</p>
        </div>
      ) : (
        questions.length > 0 && ( // Only render Quiz if questions are available
          <Quiz
            question={questions[currentQuestionIndex]}
            userAnswer={userAnswer}
            setUserAnswer={setUserAnswer}
            timeLeft={timeLeft}
            handleAnswer={handleAnswer}
          />
        )
      )}
    </div>
  );
}

export default App;
