// src/components/NumberSelection.js
import React, { useState } from 'react';

const NumberSelection = ({ onStart }) => {
  const [selectedNumbers, setSelectedNumbers] = useState([]);
  const [includeSquares, setIncludeSquares] = useState(false);

  const toggleNumber = (number) => {
    setSelectedNumbers((prevSelected) =>
      prevSelected.includes(number)
        ? prevSelected.filter((num) => num !== number) // Remove if already selected
        : [...prevSelected, number] // Add if not selected
    );
  };

  const handleStart = () => {
    if (selectedNumbers.length > 0 || includeSquares) {
      // Pass selected numbers and includeSquares to onStart function
      onStart(selectedNumbers, includeSquares);
    } else {
      alert('Please select at least one number or include squares!');
    }
  };

  return (
    <div className="number-selection-container">
      <h2>Select Times Table Numbers (2 to 12)</h2>
      <div className="number-selection">
        {[...Array(11)].map((_, index) => {
          const number = index + 2; // Create numbers from 2 to 12
          return (
            <button
              key={number}
              onClick={() => toggleNumber(number)}
              className={selectedNumbers.includes(number) ? 'selected' : ''}
            >
              {number}
            </button>
          );
        })}
      </div>
      <div>
        <label>
          <input
            type="checkbox"
            checked={includeSquares}
            onChange={() => setIncludeSquares(!includeSquares)}
          />
          Include Square Numbers (e.g., 3x3, 4x4)
        </label>
      </div>
      <button onClick={handleStart} className="start-quiz-button">
        Start Quiz
      </button>
    </div>
  );
};

export default NumberSelection;
